create table brand (id integer generated by default as identity, name varchar(255), primary key (id));
create table prices (price_id bigint generated by default as identity, currency varchar(255), end_date timestamp, price numeric(19,2), priority integer, product_id varchar(255), start_date timestamp, brand_id integer, primary key (price_id));
alter table prices add constraint fk_brand foreign key (brand_id) references brand;